            <!-- 表单实现原理：
                这个表单使用了Element UI的<el-form>组件，他是一个功能完
                整的表单解决方案，有以下功能：
                    1.数据绑定
                        1） :model="formData"：将表单数据绑定到Vue实例中的formData对象
                        2） v-model：将每个输入框与formData中的对应字段绑定
                    2.JS表单验证
                        1）:rule = "rules"：配置表单验证规则
                        2）prop：指定每个表单项对应rules中的验证规则
                    3.表单提交
                        1）@submit.prevent="submitForm"：监听表单提交事件，组织默认行为，屌用submitForm方法
                        2）ref="formRef"：获取表单实例，用于手动触发验证   
            -->

            <el-form :model="formData" :rules="rules" ref="formRef" class="account_form"  @submit.prevent="submitForm">
                <el-form-item label="用户名" prop="username" class="custom_input  custom-error-username">
                    <el-input v-model="formData.username" placeholder="请输入用户名" class="username"></el-input>
                </el-form-item>
                <el-form-item label="密码" prop="password" class="custom_input  custom-error-password">
